import React from "react";
import {Container, Row, Col, InputGroup, FormControl, Image} from "react-bootstrap"
import './Catalog.css'
import SearchLogo from './search-solid.png'
import DownLogo from './down-arrow.png'
import TopCar from './TopCar.png'
import HCar from './hyundai.png'


class CatalogPage extends React.Component {
  state = {
    model: '',
    mark: '',
    year: '',
    color: '',
    owner: ''
  };

  handleInputChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  handleSubmit = e => {
    e.preventDefault();
    if (this.state.model.trim() && this.state.mark.trim()) {
      this.props.onAddPost(this.state);
      this.handleReset();
    }
  };

  handleReset = () => {
    this.setState({
      model: '',
      mark: '',
      year: '',
      color: '',
      owner: ''
    });
  };

  render() {
    return (
        <div>
          <form onSubmit={ this.handleSubmit }>
            <div className="form-group">
              <input  type="text" placeholder="Model" className="form-control" name="model"
                      onChange={ this.handleInputChange } value={ this.state.model } />
            </div>
            <div className="form-group">
              <input  type="text" placeholder="Mark" className="form-control" name="mark"
                      onChange={ this.handleInputChange } value={ this.state.mark } />
            </div>
            <div className="form-group">
              <input  type="text" placeholder="Color" className="form-control" name="color"
                      onChange={ this.handleInputChange } value={ this.state.color } />
            </div>
            <div className="form-group">
              <input  type="text" placeholder="Year" className="form-control" name="year"
                      onChange={ this.handleInputChange } value={ this.state.year } />
            </div>
            <div className="form-group">
              <input  type="text" placeholder="Owner" className="form-control" name="owner"
                      onChange={ this.handleInputChange } value={ this.state.owner } />
            </div>
            <div className="form-group">
              <button type="submit" className="btn btn-primary">Add Post</button>
              <button type="button" className="btn btn-warning" onClick={ this.handleReset }>
                Reset
              </button>
            </div>
          </form>
        </div>
    );
  }
}

export default CatalogPage;

