import React, { Component } from 'react';
import {Container, Row, Col, Card, Image} from "react-bootstrap"
import './about.css'
import { connect } from 'react-redux';
import * as postActions from '../.././actions/postActions';
import people1 from './people1.jpg';
import people2 from './people2.jpg'


class About extends Component {
  constructor(props) {
    super(props);
    this.state = {

    }
    this.handleCreatePost = this.handleCreatePost.bind(this);
  }

  componentDidMount() {
    this.props.getPosts();
  }

  handleCreatePost() {
    let data = {
      title: "welcome to world",
      body: "hello world",
      userId: 1
    }
    this.props.createPost(data);
  }
    render() {
      console.log(this.props.firstPost);
        return (
          <Container>
            <div class="col-sm-6">
              <h1> Работа в Take My Car </h1>
              <h3> Take My Car всегда в поиске лучших профессионалов со всего мира. Работая в компании, Вы получаете не просто локальный опыт,
                а имеете возможность применить свои знания на других мировых рынках, таких как Россия и в иных странах, где функционирует Take My Car. </h3>
            </div>
            <div class="col-sm-4">
              <Image width="600px" alt="people" src={people1} id="people1"/>
            </div>
          </Container>

        );
      }
    };



  const mapStateToProps = (state) => ({
  posts: state.post.posts,
  firstPost: state.post.firstPost
})

const mapDispatchToProps = {
  getPosts: postActions.getPosts,
  createPost: postActions.createPost
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)( About);

